import React, {useState} from 'react'
import {useHistory} from 'react-router-dom'

function LoginPage(props) {
const {updateTenant, setErrors} = props

const [email, setEmail] = useState("")
const [password, setPassword] = useState("")
const [createNewAccount, setCreateNewAccount] = useState(false)
const history = useHistory()

function handleEmailChange(e){
setEmail(e.target.value)
}
function handlePasswordChange(e){
setPassword(e.target.value)
}


function handleSubmit(e){
    e.preventDefault()
    const tenant = {
    email_address: email,
    password: password
}
// debugger
console.log(tenant)
    setEmail("")
    setPassword("")

        fetch("http://localhost:3000/login", {
            method:'POST',
            headers:{
                'Content-Type': 'application/json',
                'Accept': 'application/json'
        },
            body:JSON.stringify(tenant)
        })
        .then (res => {
            if(res.ok){
                res.json().then(tenant => {
                    updateTenant(tenant)
                    history.push('/dashboard')
                })
            } else {
                res.json().then(json => setErrors(json.errors))
            }
        })
}

const handleCreateAccount = () => {
    setCreateNewAccount(!createNewAccount)
}


    return (
        <div>
            <h1>Welcome To ApartmentConnect</h1>

            {createNewAccount ? 
        <form className='login-form' onSubmit={handleSubmit}>
            <label>Email:</label>
                <input 
                type="text" 
                name="Email"
                value={email}
                onChange={handleEmailChange}
                />

                <label>Password:</label>
                <input 
                type="password" 
                name="password"
                value={password}
                onChange={handlePasswordChange}
                />
                <br></br>
                <br></br>
                <input type="submit" value="Login"/>
                <br></br>
                <br></br>
                <a onClick={handleCreateAccount}>Don't have an account? Click here to sign up!</a>
            </form>
            : 
            <form className='login-form'>
                <label>First Name:</label>
                <label>Last Name:</label>
            <label>Email:</label>
            <label>Password:</label>
            <input onClick={handleCreateAccount} type="submit" value="Submit"/>
            </form>
            }
        </div>
    )
}

export default LoginPage
